function activateNav(){var t=document.querySelectorAll("#sticky-nav .navbar-custom-item"),e=document.querySelectorAll("#regular-nav .navbar-custom-item");t.forEach(function(e){e.addEventListener("click",function(e){e.preventDefault();var r=e.currentTarget;document.querySelector(r.getAttribute("href")).scrollIntoView({behavior:"smooth"}),setTimeout(function(){t.forEach(function(t){t.classList.remove("active")}),r.classList.add("active")},400)})}),e.forEach(function(t){t.addEventListener("click",function(t){t.preventDefault();var e=t.currentTarget;document.querySelector(e.dataset.target).scrollIntoView({behavior:"smooth"})})}),document.querySelectorAll(".navbar-custom-item-extra").forEach(function(t){t.addEventListener("click",function(t){t.preventDefault();const e=t.currentTarget;document.querySelector(e.dataset.target).scrollIntoView({behavior:"smooth"})})})}